import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  REMOVE_ALERT,
  USER_LOADED,
  AUTH_ERROR
} from "../actions/types";

const initialState = {
  token: localStorage.getItem("token"),
  isAuthenticated: null,
  loading: true,
  user: null,
};

export default function (state = initialState, actions) {
  const { type, payload } = actions;

  switch (type) {
    case USER_LOADED:
      return{
        ...state,
        isAuthenticated:true,
        loading:false,
        user:payload
      }
    case REGISTER_SUCCESS:
      localStorage.setItem("token", payload.token);
      return {
        ...state,
        payload,
        isAuthenticated: true,
        loading: false,
      };
      case REGISTER_FAIL:
      case AUTH_ERROR:
        localStorage.removeItem() 
    case REMOVE_ALERT:
      localStorage.removeItem("token");
      return {
        ...state,
        token:null,
        isAuthenticated: false,
        loading: false,
      };
    default:
      return state;
  }
}
