import * as alias from 'module'
import { SET_ALERT, REMOVE_ALERT } from "./Type";
export const serAlert = (msg, alertType) => (dispatch) => {
  const id = uuid.v4();
  dispatch({
    type: SET_ALERT,
    payload: { msg,alertType,id }
  });
};
