import axios from "axios";
import { setAlert } from "./alert";
import { GET_POST, POST_ERROR , UPDATE_LIKES,DELETE_POST } from "./types";

// Get posts
export const getPosts = () => async (dispatch) => {
  try {
    const res = await axios.get("http://localhost:5000/api/posts");

    dispatch({
      type: GET_POST,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: POST_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};


// Add like
export const addLike = id => async dispatch => {
  try {
    const res = await axios.put(`http://localhost:5000/api/posts/like/${id}`);

    dispatch({
      type:UPDATE_LIKES,
      payload: {id, likes:res.data},
    });
  } catch (err) {
    dispatch({
      type: POST_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};



// Remove like
export const removeLike = id => async dispatch => {
  try {
    const res = await axios.put(`http://localhost:5000/api/posts/unlike/${id}`);

    dispatch({
      type:UPDATE_LIKES,
      payload: {id, likes:res.data},
    });
  } catch (err) {
    dispatch({
      type: POST_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};

// Delete post
export const deletePost = id => async dispatch => {
  try {
    const res = await axios.delete(`http://localhost:5000/api/posts/${id}`);

    dispatch({
      type:DELETE_POST,
      payload:id,
    });
    setAlert('Post Removed','success')
  } catch (err) {
    dispatch({
      type: POST_ERROR,
      payload: { msg: err.response.statusText, status: err.response.status },
    });
  }
};
